// 입력 데이터에서 results 배열 가져오기
const items = $input.first().json.results;

// 카테고리별로 아이템을 그룹화하고 랭크별로 정렬
const processedItems = items.reduce((acc, item) => {
  const {
    category,
    rank,
    product_name,
    start_date,
    end_date
  } = item;

  // 카테고리가 없으면 초기화
  if (!acc[category]) {
    acc[category] = {
      category: category,
      period: `${start_date} ~ ${end_date}`,
      trends: []
    };
  }

  // 랭크 5위까지만 수집
  if (rank <= 5) {
    acc[category].trends.push({
      rank: rank,
      product_name: product_name
    });
  }

  return acc;
}, {});

// 객체를 배열로 변환하고 trends를 랭크 순으로 정렬
const sortedResults = Object.values(processedItems).map(categoryData => {
  return {
    ...categoryData,
    trends: categoryData.trends.sort((a, b) => a.rank - b.rank)
  };
});

// results 키로 감싸서 반환
return {
  results: sortedResults
};

// 입력 데이터에서 results 배열 가져오기
const items = $input.first().json.results;

// 이메일 내용 생성 함수
function generateEmailContent(data) {
 // 현재 날짜 포맷팅
 const today = new Date();
 const formattedDate = today.toLocaleDateString('ko-KR', {
   year: 'numeric',
   month: 'long',
   day: 'numeric'
 });

 // 이메일 헤더
 let emailContent = `
월간 상품 트렌드 분석 리포트
작성일: ${formattedDate}
※ 본 결과는 네이버 데이터랩(Naver Datalab)의 쇼핑 트렌드 데이터를 참조하였습니다.
`;

 // 각 카테고리별 트렌드 정보 추가
 data.forEach(category => {
   emailContent += `
━━━━━━━━━━━━━━━━━━━━━
📈 ${category.category} 트렌드
기간: ${category.period}
━━━━━━━━━━━━━━━━━━━━━
`;

   // 트렌드 상품 목록 추가
   category.trends.forEach(trend => {
     emailContent += `${trend.rank}. ${trend.product_name}\n`;
   });

   // 카테고리별 분석 요약 추가
   const topProduct = category.trends[0];
   emailContent += `
- 이번 기간 ${category.category} 카테고리의 최상위 트렌드 상품은 "${topProduct.product_name}"입니다.
`;
 });

 // 이메일 푸터
 emailContent += `
━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━

※ 본 리포트는 자동으로 생성된 트렌드 분석 데이터입니다.
※ 데이터 출처: 네이버 데이터랩 쇼핑인사이트
`;

 return emailContent;
}

// 이메일 제목 생성
const emailSubject = `[트렌드 리포트] ${items[0].period} 카테고리별 상품 트렌드 분석`;

// 이메일 본문 생성
const emailBody = generateEmailContent(items);

// 결과 반환
return {
 email_data: {
   subject: emailSubject,
   body: emailBody
 }
};
